if(mch!=null){
  let bid=doc.body().attr("data-bid");
  let p=Math.ceil(String(mch.text()).match(/\d+/)[0]/900);
  let mlist=[];
  for(let i=1;i<p+1;i++){
    let url="https://www.shuchong.info/api/,"+JSON.stringify({
  "method": "POST",
  "body": "action=list&bid="+bid+"&page="+i
});
    Array.prototype.push.apply(mlist,
JSON.parse(java.ajax(url)).data.map(ch=>"<li><a href=\"/chapter/"+bid+"/"+ch.id+".html\">"+ch.cN+"</a>"+ch.uT+"</li>")
    );
  }
  mch.html(mlist.join("\n"));
}

doc.select(".chapter-list li").toArray().map(ch=>{
