+<js>
var bid = parseInt(result.match(/data-bid="(\d+)/)[1]);
var reg = 'href="/chapter/[^/]+/([^"]+)[^>]+>([^<]+)[^>]+>([^<]+)';
var cList = result.match(new RegExp(reg, 'g')).map((item) => {
	var ret = item.match(reg);
	return { cN: ret[2], uT: ret[3].trim(), id: parseInt(ret[1]) + bid };
});
var hider = result.match(/æŸ¥çœ‹éšè—ç« èŠ‚[^<]+/);
if (hider) {
	let p = Math.ceil(hider[0].match(/\d+/)[0] / 900);
	for (let i = 1; i <= p; ++i) {
		let url = 'https://www.zhaishuyuan.com/api/,' + JSON.stringify({ charset: 'gbk', method: 'POST', body: 'action=list&bid=' + bid + '&page=' + i });
		Array.prototype.push.apply(cList, JSON.parse(java.ajax(url)).data);
	}
}
cList.sort((a, b) => (a.id < b.id ? -1 : 1)).map((item) => {
	item.id = '/chapter/' + bid + '/' + (item.id - bid);
	return item;
});
